#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="E:\Study\MVC_Web\jollicow_be\node_modules\.pnpm\release-it@17.10.0\node_modules\release-it\bin\node_modules;E:\Study\MVC_Web\jollicow_be\node_modules\.pnpm\release-it@17.10.0\node_modules\release-it\node_modules;E:\Study\MVC_Web\jollicow_be\node_modules\.pnpm\release-it@17.10.0\node_modules;E:\Study\MVC_Web\jollicow_be\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/e/Study/MVC_Web/jollicow_be/node_modules/.pnpm/release-it@17.10.0/node_modules/release-it/bin/node_modules:/mnt/e/Study/MVC_Web/jollicow_be/node_modules/.pnpm/release-it@17.10.0/node_modules/release-it/node_modules:/mnt/e/Study/MVC_Web/jollicow_be/node_modules/.pnpm/release-it@17.10.0/node_modules:/mnt/e/Study/MVC_Web/jollicow_be/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../../release-it@17.10.0/node_modules/release-it/bin/release-it.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../../release-it@17.10.0/node_modules/release-it/bin/release-it.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../../../release-it@17.10.0/node_modules/release-it/bin/release-it.js" $args
  } else {
    & "node$exe"  "$basedir/../../../../../../release-it@17.10.0/node_modules/release-it/bin/release-it.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
